import React, {useState} from 'react';
import { capitalizeFirstLetter } from '../../utils/helpers'

function Nav() {
       const [catagoties] = useState([
        {
            name: "commercial",
            description:
             "Photos of grocery stores, food trucks, and other commercial projects"
        },
        { name: "portraits", description: "Portraits of people in my life"},
        { name: "food", description: "Delicious delicacies"},
        {
            name: "landscape",
            description: "Fields, farmhouses, waterfalls, and the beauty of nature"
        }
    ]);
    const [currentCatagory, setCurrentCategory] = useState(catagoties[0])


    return (
        <header>
            <h2> 
                <a data-testid="link" href="/">
                <span role="img" aria-label="camera">ðŸ“¸</span> Oh Snap!
                </a>
            </h2>
            <nav>
                <ul className="flex-row">
                    <li className="mx-2">
                        <a data-testid="about" href="#about">
                            About me
                        </a>
                    </li>
                    <li>
                        <span>Contact</span>
                    </li>
                        {catagoties.map((catagory)=>(
                            <li className={`mx-1 ${ currentCatagory.name === catagory.name && 'navActive'}`}
                            key={catagory.name}>
                                <span onClick={()=> {
                                    setCurrentCategory(catagory)
                                }}>
                                    {capitalizeFirstLetter(catagory.name)}
                                </span>
                            </li>
                        ))}
                </ul>
            </nav>
        </header>
    )
}

export default Nav;
